import React, { useState, useEffect } from 'react';
import { Table, Card, Alert, Spinner, Form, Row, Col, Badge, Button } from 'react-bootstrap';
import { useSearchParams } from 'react-router-dom';
import { ordersApi } from '../services/api';

const OrdersList = ({ selectedAccount }) => {
  const [searchParams] = useSearchParams();
  const symbolFilter = searchParams.get('symbol');

  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    status: '',
    symbol: symbolFilter || ''
  });

  useEffect(() => {
    if (selectedAccount) {
      loadOrders();
    }
  }, [selectedAccount]);

  const loadOrders = async () => {
    if (!selectedAccount) return;

    try {
      setLoading(true);
      setError(null);

      const response = await ordersApi.getOrdersByAccount(selectedAccount.id);
      setOrders(response.data);
    } catch (err) {
      console.error('Failed to load orders:', err);
      setError('Failed to load orders');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const applyFilters = () => {
    loadOrders();
  };

  // Filter orders based on current filters
  const filteredOrders = orders.filter(order => {
    if (filters.status && order.status !== filters.status) return false;
    if (filters.symbol && !order.symbolTicker.toLowerCase().includes(filters.symbol.toLowerCase())) return false;
    return true;
  });

  const formatCurrency = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(value);
  };

  const formatDateTime = (dateTime) => {
    if (!dateTime) return 'N/A';
    const date = new Date(dateTime);
    const day = date.getDate();
    const month = date.toLocaleString('en-US', { month: 'long' });
    const time = date.toLocaleString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
    return `${day} ${month}, ${time}`;
  };

  const getStatusBadge = (status) => {
    const statusColors = {
      'FILLED': 'success',
      'PARTIALLY_FILLED': 'warning',
      'PENDING': 'info',
      'CANCELLED': 'secondary',
      'REJECTED': 'danger'
    };

    return (
      <Badge bg={statusColors[status] || 'secondary'}>
        {status}
      </Badge>
    );
  };

  const getSideBadge = (side) => {
    return (
      <Badge bg={side === 'BUY' ? 'success' : 'danger'}>
        {side}
      </Badge>
    );
  };

  if (!selectedAccount) {
    return (
      <div className="text-center py-5">
        <i className="bi bi-list-ul display-1 text-muted"></i>
        <h3 className="mt-3">No Trading Account Selected</h3>
        <p className="text-muted">Please select a trading account to view orders.</p>
      </div>
    );
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          <i className="bi bi-list-ul me-2"></i>
          Orders
        </h2>
        <div className="text-muted">
          Account: <strong>{selectedAccount.accountName}</strong>
        </div>
      </div>

      {/* Filters */}
      <Card className="mb-4">
        <Card.Body>
          <Row className="align-items-end">
            <Col md={2}>
              <Form.Group>
                <Form.Label>Status</Form.Label>
                <Form.Select
                  value={filters.status}
                  onChange={(e) => handleFilterChange('status', e.target.value)}
                >
                  <option value="">All Statuses</option>
                  <option value="FILLED">Filled</option>
                  <option value="PARTIALLY_FILLED">Partially Filled</option>
                  <option value="PENDING">Pending</option>
                  <option value="CANCELLED">Cancelled</option>
                  <option value="REJECTED">Rejected</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={2}>
              <Form.Group>
                <Form.Label>Symbol</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Filter by symbol..."
                  value={filters.symbol}
                  onChange={(e) => handleFilterChange('symbol', e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={3}>
              <Form.Group>
                <Form.Label>Start Date</Form.Label>
                <Form.Control
                  type="date"
                  value={filters.startDate}
                  onChange={(e) => handleFilterChange('startDate', e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={3}>
              <Form.Group>
                <Form.Label>End Date</Form.Label>
                <Form.Control
                  type="date"
                  value={filters.endDate}
                  onChange={(e) => handleFilterChange('endDate', e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={3}>
              <Button variant="primary" onClick={applyFilters} disabled={loading}>
                {loading ? <Spinner animation="border" size="sm" /> : 'Apply Filters'}
              </Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {error && (
        <Alert variant="danger" className="mb-4">
          <i className="bi bi-exclamation-triangle me-2"></i>
          {error}
        </Alert>
      )}

      <Card>
        <Card.Header>
          <div className="d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Orders List</h5>
            <Badge bg="info">{orders.length} orders</Badge>
          </div>
        </Card.Header>
        <Card.Body className="p-0">
          {loading ? (
            <div className="text-center py-5">
              <Spinner animation="border" variant="primary" />
              <p className="mt-3">Loading orders...</p>
            </div>
          ) : orders.length > 0 ? (
            <div className="table-responsive">
              <Table striped hover variant="dark" className="mb-0">
                <thead>
                  <tr>
                    <th>Symbol</th>
                    <th>Side</th>
                    <th>Type</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Filled</th>
                    <th>Avg Trades/Day</th>
                    <th>P/L Amount</th>
                    <th>Last Traded Date</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredOrders.map(order => (
                    <tr key={order.id}>
                      <td>
                        <strong>{order.symbolTicker}</strong>
                      </td>
                      <td>
                        {getSideBadge(order.side)}
                      </td>
                      <td>
                        <Badge bg="secondary">{order.type}</Badge>
                      </td>
                      <td>{order.quantity}</td>
                      <td>{formatCurrency(order.price)}</td>
                      <td>
                        {order.filledQuantity || 0} / {order.quantity}
                      </td>
                      <td>
                        <span className="text-info">
                          {order.avgTradesPerDay ? Number(order.avgTradesPerDay).toFixed(2) : 'N/A'}
                        </span>
                      </td>
                      <td>
                        <span className={order.realizedPnl >= 0 ? 'text-success' : 'text-danger'}>
                          {formatCurrency(order.realizedPnl)}
                        </span>
                      </td>
                      <td>
                        <small>{formatDateTime(order.lastTradedDate)}</small>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </div>
          ) : (
            <div className="text-center py-5">
              <i className="bi bi-inbox display-1 text-muted"></i>
              <h4 className="mt-3">No Orders Found</h4>
              <p className="text-muted">No orders found for the selected account and filters.</p>
            </div>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default OrdersList;
